import React from 'react';
import { motion } from 'framer-motion';
import { Meld, MeldType, GangType, tileToString } from '../types/mahjong';

interface KongActionHistoryProps {
  melds: Meld[];
  currentPlayer: number;
  className?: string;
}

const KongActionHistory: React.FC<KongActionHistoryProps> = ({
  melds,
  currentPlayer,
  className = ''
}) => {
  // 获取杠牌操作列表 - 过滤掉直杠（明杠），因为直杠信息已经直接显示在杠牌上
  const kongMelds = melds.filter(meld => 
    meld.type === MeldType.GANG && 
    meld.gang_type !== GangType.MING_GANG  // 排除直杠（明杠）
  );

  // 获取杠牌类型的中文名称
  const getKongTypeName = (gangType?: GangType): string => {
    switch (gangType) {
      case GangType.MING_GANG:
        return '明杠';
      case GangType.AN_GANG:
        return '暗杠';
      case GangType.JIA_GANG:
        return '加杠';
      default:
        return '杠';
    }
  };

  // 获取玩家名称
  const getPlayerName = (playerId: number): string => {
    const playerNames = ['我', '下家', '对家', '上家'];
    return playerNames[playerId] || `玩家${playerId + 1}`;
  };

  // 获取操作描述
  const getActionDescription = (meld: Meld): string => {
    if (!meld.tiles.length) return '';
    
    const tileName = tileToString(meld.tiles[0]);
    const kongType = getKongTypeName(meld.gang_type);
    
    if (meld.gang_type === GangType.MING_GANG && meld.source_player !== undefined) {
      const sourceName = getPlayerName(meld.source_player);
      return `${kongType}了${sourceName}的${tileName}`;
    } else if (meld.gang_type === GangType.JIA_GANG) {
      return `${kongType} ${tileName}`;
    } else {
      return `${kongType} ${tileName}`;
    }
  };

  // 获取时间显示
  const getTimeDisplay = (timestamp?: number): string => {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    return date.toLocaleTimeString('zh-CN', { 
      hour: '2-digit', 
      minute: '2-digit',
      second: '2-digit'
    });
  };

  if (kongMelds.length === 0) {
    return (
      <div className={`kong-action-history bg-white rounded-lg shadow-md p-3 ${className}`}>
        <h4 className="text-sm font-semibold text-gray-700 mb-2 flex items-center">
          <span className="w-2 h-2 bg-orange-500 rounded-full mr-2"></span>
          杠牌记录（暗杠·加杠）
        </h4>
        <div className="text-xs text-gray-500 text-center py-4">
          暂无暗杠/加杠记录
          <br />
          <span className="text-gray-400">直杠信息显示在杠牌上</span>
        </div>
      </div>
    );
  }

  return (
    <motion.div
      className={`kong-action-history bg-white rounded-lg shadow-md p-3 ${className}`}
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
    >
      <h4 className="text-sm font-semibold text-gray-700 mb-2 flex items-center">
        <span className="w-2 h-2 bg-orange-500 rounded-full mr-2"></span>
        杠牌记录（暗杠·加杠）
      </h4>
      
      <div className="space-y-2">
        {kongMelds.map((meld, index) => (
          <motion.div
            key={meld.id || index}
            className="flex items-center justify-between text-xs bg-gray-50 rounded p-2"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <div className="flex items-center space-x-2">
              {/* 杠牌类型图标 */}
              <div className={`w-4 h-4 rounded-full flex items-center justify-center text-white text-xs font-bold ${
                meld.gang_type === GangType.MING_GANG ? 'bg-red-500' :
                meld.gang_type === GangType.AN_GANG ? 'bg-gray-500' :
                'bg-yellow-500'
              }`}>
                {meld.gang_type === GangType.MING_GANG ? '明' :
                 meld.gang_type === GangType.AN_GANG ? '暗' : '加'}
              </div>
              
              {/* 操作描述 */}
              <span className="text-gray-700 font-medium">
                {getActionDescription(meld)}
              </span>
            </div>
            
            {/* 时间戳 */}
            {meld.timestamp && (
              <span className="text-gray-400">
                {getTimeDisplay(meld.timestamp)}
              </span>
            )}
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default KongActionHistory;
