import React from 'react';
import { motion } from 'framer-motion';
import { useGameStore, selectPlayerHand } from '../stores/gameStore';
import { Tile, MeldType, GangType, calculateRemainingTiles } from '../types/mahjong';
import MahjongTile from './MahjongTile';

interface MahjongTableProps {
  className?: string;
}

const MahjongTable: React.FC<MahjongTableProps> = ({ className }) => {
  const gameState = useGameStore(state => state.gameState);
  const player0Hand = useGameStore(selectPlayerHand(0));
  const player1Hand = useGameStore(selectPlayerHand(1));
  const player2Hand = useGameStore(selectPlayerHand(2));
  const player3Hand = useGameStore(selectPlayerHand(3));

  const player0Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[0] || []);
  const player1Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[1] || []);
  const player2Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[2] || []);
  const player3Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[3] || []);

  const remainingTiles = calculateRemainingTiles(gameState);
  const playerNames = ['我', '下家', '对家', '上家'];

  // 渲染碰牌杠牌
  const renderMelds = (melds: any[], position: string) => {
    if (melds.length === 0) return null;

    const flexDirection = position === 'left' || position === 'right' ? 'flex-col' : 'flex-row';

    return (
      <div className={`flex ${flexDirection} gap-2`}>
        {melds.map((meld, index) => (
          <div 
            key={`meld-${index}`}
            className={`flex ${flexDirection} gap-0 p-1 rounded bg-white bg-opacity-20`}
          >
            {meld.tiles.map((tile: Tile, tileIndex: number) => (
              <div
                key={`meld-tile-${index}-${tileIndex}`}
                className={
                  position === 'left' ? 'transform -rotate-90' :
                  position === 'right' ? 'transform rotate-90' :
                  position === 'top' ? 'transform rotate-180' :
                  ''
                }
              >
                <MahjongTile
                  tile={tile}
                  size="tiny"
                  variant={meld.type === MeldType.GANG && meld.gang_type === GangType.AN_GANG && tileIndex === 1 ? "disabled" : "default"}
                  seamless={true}
                />
              </div>
            ))}
            <div className={`text-xs text-white text-center ${
              position === 'left' || position === 'right' ? 'transform -rotate-90' : ''
            }`}>
              {meld.type === MeldType.PENG ? '碰' : 
               meld.type === MeldType.GANG && meld.gang_type === GangType.MING_GANG ? '明杠' :
               meld.type === MeldType.GANG && meld.gang_type === GangType.AN_GANG ? '暗杠' : '吃'}
            </div>
          </div>
        ))}
      </div>
    );
  };

  // 渲染手牌
  const renderHand = (hand: any, position: string) => {
    const flexDirection = position === 'left' || position === 'right' ? 'flex-col' : 'flex-row';

    return (
      <div className={`flex ${flexDirection} gap-0`}>
        {hand.tiles.map((tile: Tile, index: number) => (
          <div
            key={`hand-${index}`}
            className={
              position === 'left' ? 'transform -rotate-90' :
              position === 'right' ? 'transform rotate-90' :
              position === 'top' ? 'transform rotate-180' :
              ''
            }
          >
            <MahjongTile
              tile={tile}
              size="small"
              variant="default"
              seamless={true}
              direction={position === 'left' || position === 'right' ? 'vertical' : 'horizontal'}
            />
          </div>
        ))}
      </div>
    );
  };

  // 渲染玩家弃牌
  const renderDiscards = (discards: Tile[], position: string) => {
    if (discards.length === 0) return null;

    const flexDirection = position === 'left' || position === 'right' ? 'flex-col' : 'flex-row';
    const maxDiscards = 8; // 限制显示的弃牌数量
    const visibleDiscards = discards.slice(-maxDiscards);

    return (
      <div className="bg-white bg-opacity-10 rounded p-1">
        <div className={`text-xs text-white text-center mb-1 ${
          position === 'left' || position === 'right' ? 'transform -rotate-90' : ''
        }`}>
          弃牌({discards.length})
        </div>
        <div className={`flex ${flexDirection} gap-0 flex-wrap`}>
          {visibleDiscards.map((tile, index) => (
            <div
              key={`discard-${index}`}
              className={
                position === 'left' ? 'transform -rotate-90' :
                position === 'right' ? 'transform rotate-90' :
                position === 'top' ? 'transform rotate-180' :
                ''
              }
            >
              <MahjongTile
                tile={tile}
                size="tiny"
                variant="disabled"
                seamless={true}
                animationDelay={index * 0.02}
              />
            </div>
          ))}
          {discards.length > maxDiscards && (
            <div className={`text-xs text-white opacity-60 ${
              position === 'left' || position === 'right' ? 'transform -rotate-90' : ''
            }`}>
              ...
            </div>
          )}
        </div>
      </div>
    );
  };

  // 渲染牌桌中心的剩余牌数和围绕的弃牌
  const renderTableCenter = () => {
    // 获取所有弃牌按玩家分组
    const allDiscards = [
      ...player0Discards,
      ...player1Discards, 
      ...player2Discards,
      ...player3Discards
    ];

    return (
      <div className="relative flex items-center justify-center h-full w-full">
        {/* 中心剩余牌数显示 */}
        <div className="absolute z-10 text-center text-white bg-white bg-opacity-20 rounded-lg p-4">
          <motion.div
            animate={{ scale: [1, 1.05, 1] }}
            transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
            className="text-4xl font-bold mb-1"
          >
            {remainingTiles}
          </motion.div>
          <div className="text-sm opacity-80">剩余</div>
        </div>

        {/* 围绕中心的弃牌区域 - 回字形排列 */}
        {allDiscards.length > 0 && (
          <>
            {/* 上方弃牌 */}
            <div className="absolute top-2 left-1/2 transform -translate-x-1/2">
              <div className="flex gap-0">
                {allDiscards.slice(0, Math.min(6, allDiscards.length)).map((tile, index) => (
                  <MahjongTile
                    key={`center-top-${index}`}
                    tile={tile}
                    size="tiny"
                    variant="disabled"
                    seamless={true}
                    animationDelay={index * 0.02}
                  />
                ))}
              </div>
            </div>

            {/* 右方弃牌 */}
            <div className="absolute right-2 top-1/2 transform -translate-y-1/2">
              <div className="flex flex-col gap-0">
                {allDiscards.slice(6, Math.min(12, allDiscards.length)).map((tile, index) => (
                  <div key={`center-right-${index}`} className="transform rotate-90">
                    <MahjongTile
                      tile={tile}
                      size="tiny"
                      variant="disabled"
                      seamless={true}
                      animationDelay={(index + 6) * 0.02}
                    />
                  </div>
                ))}
              </div>
            </div>

            {/* 下方弃牌 */}
            <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2">
              <div className="flex gap-0">
                {allDiscards.slice(12, Math.min(18, allDiscards.length)).map((tile, index) => (
                  <div key={`center-bottom-${index}`} className="transform rotate-180">
                    <MahjongTile
                      tile={tile}
                      size="tiny"
                      variant="disabled"
                      seamless={true}
                      animationDelay={(index + 12) * 0.02}
                    />
                  </div>
                ))}
              </div>
            </div>

            {/* 左方弃牌 */}
            <div className="absolute left-2 top-1/2 transform -translate-y-1/2">
              <div className="flex flex-col gap-0">
                {allDiscards.slice(18, Math.min(24, allDiscards.length)).map((tile, index) => (
                  <div key={`center-left-${index}`} className="transform -rotate-90">
                    <MahjongTile
                      tile={tile}
                      size="tiny"
                      variant="disabled"
                      seamless={true}
                      animationDelay={(index + 18) * 0.02}
                    />
                  </div>
                ))}
              </div>
            </div>
          </>
        )}
      </div>
    );
  };

  // 渲染玩家区域 - 重新设计布局
  const renderPlayerArea = (playerId: number, position: string) => {
    const hand = [player0Hand, player1Hand, player2Hand, player3Hand][playerId];
    const discards = [player0Discards, player1Discards, player2Discards, player3Discards][playerId];

    // 根据位置确定布局方向
    const isVertical = position === 'left' || position === 'right';
    const flexDirection = isVertical ? 'flex-col' : 'flex-row';

    // 确定排列顺序：从桌子边沿向内
    const renderOrder = () => {
      const elements = [];

      // 1. 玩家昵称（最靠近边沿）
      elements.push(
        <div 
          key="nickname"
          className={`text-white text-sm font-bold text-center ${
            isVertical ? 'transform -rotate-90 whitespace-nowrap' : ''
          }`}
        >
          {playerNames[playerId]} ({hand.tiles.length + hand.melds.reduce((sum, meld) => sum + meld.tiles.length, 0)}张)
        </div>
      );

      // 2. 手牌和碰杠牌（中间区域）
      const handAndMeldsArea = (
        <div key="hand-melds" className={`flex ${flexDirection} items-center gap-2`}>
          {/* 手牌区域 */}
          <div className="flex-shrink-0">
            {renderHand(hand, position)}
          </div>

          {/* 碰牌杠牌区域 - 半个麻将牌的空隙 */}
          {hand.melds.length > 0 && (
            <div className="flex-shrink-0" style={{ marginLeft: isVertical ? '0' : '12px', marginTop: isVertical ? '12px' : '0' }}>
              {renderMelds(hand.melds, position)}
            </div>
          )}
        </div>
      );
      elements.push(handAndMeldsArea);

      // 3. 弃牌区域（最远离边沿）
      if (discards.length > 0) {
        elements.push(
          <div key="discards" className="flex-shrink-0">
            {renderDiscards(discards, position)}
          </div>
        );
      }

      return elements;
    };

    return (
      <div className={`flex ${flexDirection} items-center justify-center gap-3 h-full w-full p-2`}>
        {renderOrder()}
      </div>
    );
  };

  return (
    <div className={`bg-gradient-to-br from-green-800 to-green-900 rounded-2xl relative ${className}`} 
         style={{ 
           width: '800px',
           minWidth:'800px',
           minHeight:'800px',
           height: '800px',
           padding: '40px'
         }}>

      {/* 对家区域（上方） */}
      <div className="absolute top-0 left-1/2 transform -translate-x-1/2 w-96 h-24 flex items-start justify-center">
        {renderPlayerArea(2, 'top')}
      </div>

      {/* 上家区域（左侧） */}
      <div className="absolute left-0 top-1/2 transform -translate-y-1/2 w-24 h-96 flex items-center justify-start">
        {renderPlayerArea(3, 'left')}
      </div>

      {/* 中央区域 */}
      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-80 h-80 bg-white bg-opacity-10 rounded-2xl border-2 border-white border-opacity-20">
        {renderTableCenter()}
      </div>

      {/* 下家区域（右侧） */}
      <div className="absolute right-0 top-1/2 transform -translate-y-1/2 w-24 h-96 flex items-center justify-end">
        {renderPlayerArea(1, 'right')}
      </div>

      {/* 我的区域（下方） */}
      <div className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-96 h-24 flex items-end justify-center">
        {renderPlayerArea(0, 'bottom')}
      </div>
    </div>
  );
};

export default MahjongTable;
