        pattern: (
          <div className="flex flex-col items-center justify-center text-white">
            <div className="text-xs opacity-90">üÄÑ</div>
            <div className="w-full h-0.5 bg-white/30 my-0.5"></div>
            <div className="text-xs opacity-90">üÄÑ</div>
          </div>
        )
      }
    },
    {
      id: 'elegant',
      name: 'Á¥†ÈõÖÁôΩÁéâ',
