import React from 'react';
import classNames from 'classnames';

interface SourceIndicatorProps {
  sourcePlayer: number;
  currentPlayer: number;
  position: 'bottom' | 'right' | 'top' | 'left';
  className?: string;
}

const SourceIndicator: React.FC<SourceIndicatorProps> = ({
  sourcePlayer,
  currentPlayer,
  position,
  className
}) => {
  const getDirection = (): 'bottom' | 'right' | 'top' | 'left' => {
    // 根据玩家位置计算方向
    const positions = ['bottom', 'right', 'top', 'left'] as const;
    const currentIndex = positions.indexOf(position);
    const sourceIndex = (currentIndex + sourcePlayer - currentPlayer + 4) % 4;
    return positions[sourceIndex];
  };

  const getArrowIcon = () => {
    const direction = getDirection();
    const arrowMap = {
      'bottom': '↓',
      'right': '→', 
      'top': '↑',
      'left': '←'
    } as const;
    return arrowMap[direction];
  };

  const getPlayerName = () => {
    const direction = getDirection();
    const nameMap = {
      'bottom': '下家',
      'right': '右家',
      'top': '上家', 
      'left': '左家'
    } as const;
    return nameMap[direction];
  };

  return (
    <div 
      className={classNames(
        "absolute -top-2 -right-2 bg-red-500 text-white text-xs w-6 h-6 rounded-full flex items-center justify-center shadow-lg",
        "hover:bg-red-600 transition-colors duration-200",
        "cursor-help",
        className
      )}
      title={`来自${getPlayerName()}`}
    >
      {getArrowIcon()}
    </div>
  );
};

export default SourceIndicator;
