import React from 'react';
import { motion } from 'framer-motion';
import classNames from 'classnames';
import MahjongTile from './MahjongTile';
import SourceIndicator from './SourceIndicator';
import { KongMeld, tileToString } from '../types/mahjong';

interface KongDisplayProps {
  kong: KongMeld;
  playerPosition: 'bottom' | 'right' | 'top' | 'left';
  className?: string;
  showTooltip?: boolean;
  cardBackStyle?: 'classic' | 'elegant' | 'bamboo' | 'cloud' | 'traditional' | 'pure';
  isCurrentPlayer?: boolean;
}

const KongDisplay: React.FC<KongDisplayProps> = ({ 
  kong, 
  playerPosition, 
  className,
  showTooltip = true,
  cardBackStyle = 'elegant',
  isCurrentPlayer = false
}) => {
  const getCardLayout = () => {
    if (kong.type === 'direct_kong') {
      return getDirectKongLayout();
    } else if (kong.type === 'added_kong') {
      return getAddedKongLayout();
    } else {
      return getConcealedKongLayout();
    }
  };

  const getDirectKongLayout = () => {
    // 直杠布局：3张竖放 + 1张横放（表示来源）
    return (
      <div className={classNames('kong-container direct-kong flex items-end', className)}>
        {/* 前3张牌竖放 */}
        <div className="vertical-cards flex">
          {kong.tiles.slice(0, 3).map((tile, index) => (
            <MahjongTile
              key={`${kong.id}-vertical-${index}`}
              tile={tile}
              size="small"
              className="mr-1"
              seamless={true}
              direction="horizontal"
              cardBackStyle={cardBackStyle}
            />
          ))}
        </div>
        
        {/* 第4张牌横放，表示来自其他玩家 */}
        <div className="horizontal-card relative ml-2">
          <MahjongTile
            tile={kong.tiles[3]}
            size="small"
            className="transform rotate-90"
            cardBackStyle={cardBackStyle}
          />
          
          {/* 来源指示器 */}
          {kong.source_player !== undefined && (
            <SourceIndicator 
              sourcePlayer={kong.source_player}
              currentPlayer={kong.kong_player}
              position={playerPosition}
            />
          )}
        </div>
      </div>
    );
  };

  const getAddedKongLayout = () => {
    // 加杠布局：原碰牌3张正面 + 新加1张横向显示（disabled样式）
    return (
      <div className={classNames('kong-container added-kong flex items-end', className)}>
        {/* 原来的3张碰牌 - 正面显示 */}
        <div className="original-peng flex">
          {kong.tiles.slice(0, 3).map((tile, index) => (
            <MahjongTile
              key={`${kong.id}-original-${index}`}
              tile={tile}
              size="small"
              className="mr-1"
              seamless={true}
              direction="horizontal"
              cardBackStyle={cardBackStyle}
            />
          ))}
        </div>
        
        {/* 新加的第4张牌 - 横向显示，disabled样式 */}
        <div className="added-card relative ml-2">
          <MahjongTile
            tile={kong.tiles[3]}
            size="small"
            variant="disabled"
            className="transform rotate-90 border-2 border-yellow-400 shadow-lg"
            cardBackStyle={cardBackStyle}
          />
          
          {/* 加杠标识 */}
          <div className="absolute -top-1 -right-1 bg-yellow-400 text-black text-xs px-1 rounded font-bold shadow-sm z-20">
            加
          </div>
        </div>
      </div>
    );
  };

  const getConcealedKongLayout = () => {
    // 暗杠布局：当前玩家显示selected样式，其他玩家显示背面
    const tileVariant = isCurrentPlayer ? 'selected' : 'back';
    
    console.log('getConcealedKongLayout:', {
      isCurrentPlayer,
      tileVariant,
      kongId: kong.id
    });
    
    return (
      <div className={classNames('kong-container concealed-kong flex items-end', className)}>
        {/* 根据是否是当前玩家决定显示方式 */}
        <div className="concealed-cards flex">
          {kong.tiles.map((tile, index) => {
            // 临时强制所有牌显示为selected进行测试
            const currentVariant = isCurrentPlayer ? 'selected' : 'back';
            console.log(`Tile ${index}:`, {
              tile,
              variant: currentVariant,
              index,
              isCurrentPlayer
            });
            return (
              <MahjongTile
                key={`${kong.id}-concealed-${index}-${isCurrentPlayer}`}
                tile={tile}
                size="small"
                variant={currentVariant}
                className={`mr-1 ${isCurrentPlayer ? 'force-selected' : ''}`}
                seamless={false}
                direction="horizontal"
                cardBackStyle={cardBackStyle}
              />
            );
          })}
        </div>
        
        {/* 暗杠标识 */}
        <div className="absolute -top-2 -right-2 bg-gray-600 text-white text-xs px-1 rounded font-bold shadow-sm z-20">
          暗
        </div>
      </div>
    );
  };

  const getTooltipText = () => {
    if (kong.type === 'direct_kong') {
      return `直杠 - 杠了玩家${(kong.source_player || 0) + 1}的${tileToString(kong.card)}`;
    } else if (kong.type === 'added_kong') {
      return `加杠 - 在原有碰牌基础上加杠${tileToString(kong.card)}`;
    } else {
      return `暗杠 - 暗中杠了${tileToString(kong.card)}`;
    }
  };

  const containerClasses = classNames(
    'kong-display-container relative p-2 bg-gray-50 rounded-lg border border-gray-200 shadow-sm',
    'hover:shadow-md transition-shadow duration-200',
    {
      [`player-${playerPosition}`]: true,
    }
  );

  return (
    <motion.div
      className={containerClasses}
      initial={{ scale: 0.8, opacity: 0 }}
      animate={{ scale: 1, opacity: 1 }}
      transition={{ duration: 0.5, type: "spring", stiffness: 120 }}
    >
      {getCardLayout()}
      
      {/* 悬停提示 */}
      {showTooltip && (
        <div className="tooltip-container group">
          <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-2 py-1 bg-black text-white text-xs rounded opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap z-10">
            {getTooltipText()}
            {/* 小箭头 */}
            <div className="absolute top-full left-1/2 transform -translate-x-1/2 w-0 h-0 border-l-2 border-r-2 border-t-2 border-transparent border-t-black"></div>
          </div>
        </div>
      )}
    </motion.div>
  );
};

export default KongDisplay;
