import React from 'react';
import { motion } from 'framer-motion';

interface SimpleSourceIndicatorProps {
  sourcePlayer: number;
  currentPlayer: number;
  className?: string;
}

const SimpleSourceIndicator: React.FC<SimpleSourceIndicatorProps> = ({ 
  sourcePlayer, 
  currentPlayer,
  className = ''
}) => {
  // 获取来源玩家的简短标识
  const getSourceLabel = (): string => {
    if (sourcePlayer === currentPlayer) return '我';
    
    // 计算相对位置
    const diff = (sourcePlayer - currentPlayer + 4) % 4;
    const labels = ['我', '下', '对', '上'] as const;
    return labels[diff] || '对';  // 默认显示"对"
  };

  // 获取提示文本
  const getTooltipText = (): string => {
    const label = getSourceLabel();
    if (label === '我') return '杠了自己的弃牌';
    return `杠了${label}家的弃牌`;
  };

  return (
    <motion.div
      className={`absolute -top-1 -right-1 w-4 h-4 bg-red-600 text-white text-xs font-bold rounded-sm flex items-center justify-center shadow-md z-30 ${className}`}
      initial={{ scale: 0, opacity: 0 }}
      animate={{ scale: 1, opacity: 1 }}
      transition={{ 
        delay: 0.2, 
        type: "spring", 
        stiffness: 300,
        damping: 20
      }}
      title={getTooltipText()}
    >
      {getSourceLabel()}
    </motion.div>
  );
};

export default SimpleSourceIndicator;
