import React from 'react';
import { motion } from 'framer-motion';
import { useGameStore, selectPlayerDiscardedTiles } from '../stores/gameStore';
import { Tile } from '../types/mahjong';
import MahjongTile from './MahjongTile';

interface MahjongTableProps {
  className?: string;
}

const MahjongTable: React.FC<MahjongTableProps> = ({ className }) => {
  const player0Discards = useGameStore(selectPlayerDiscardedTiles(0));
  const player1Discards = useGameStore(selectPlayerDiscardedTiles(1));
  const player2Discards = useGameStore(selectPlayerDiscardedTiles(2));
  const player3Discards = useGameStore(selectPlayerDiscardedTiles(3));

  const playerNames = ['我', '下家', '对家', '上家'];
  const playerColors = [
    'bg-blue-100 border-blue-300',
    'bg-green-100 border-green-300', 
    'bg-red-100 border-red-300',
    'bg-yellow-100 border-yellow-300'
  ];

  const renderDiscardArea = (playerId: number, discards: Tile[], position: string) => {
    const isVertical = position === 'left' || position === 'right';
    
    return (
      <div className={`discarded-area ${position}`}>
        <div className={`text-sm font-medium mb-2 text-center ${
          position === 'top' ? 'text-red-600' : 
          position === 'left' ? 'text-green-600' :
          position === 'right' ? 'text-yellow-600' :
          'text-blue-600'
        }`}>
          {playerNames[playerId]} ({discards.length}张)
        </div>
        
        <div className={`min-h-16 ${playerColors[playerId]} rounded-lg p-2 border-2 border-dashed`}>
          {discards.length > 0 ? (
            <div className={`flex ${isVertical ? 'flex-col' : 'flex-row'} gap-1 flex-wrap ${
              isVertical ? 'items-center' : 'justify-center'
            }`}>
              {discards.map((tile, index) => (
                <MahjongTile
                  key={`discard-${playerId}-${index}`}
                  tile={tile}
                  size="tiny"
                  variant="disabled"
                  animationDelay={index * 0.02}
                />
              ))}
            </div>
          ) : (
            <div className="text-center text-gray-500 py-2 text-xs">
              暂无弃牌
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className={`mahjong-table ${className}`}>
      <style jsx>{`
        .mahjong-table {
          position: relative;
          width: 100%;
          height: 100%;
          min-height: 400px;
          background: radial-gradient(circle, #2d5a27 0%, #1a3d18 100%);
          border-radius: 20px;
          padding: 20px;
          display: grid;
          grid-template-areas: 
            ". top ."
            "left center right"
            ". bottom .";
          grid-template-rows: 1fr 2fr 1fr;
          grid-template-columns: 1fr 2fr 1fr;
          gap: 10px;
        }
        
        .discarded-area.top {
          grid-area: top;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }
        
        .discarded-area.bottom {
          grid-area: bottom;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }
        
        .discarded-area.left {
          grid-area: left;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }
        
        .discarded-area.right {
          grid-area: right;
          display: flex;
          flex-direction: column;
          justify-content: center;
        }
        
        .center-area {
          grid-area: center;
          display: flex;
          align-items: center;
          justify-content: center;
          background: rgba(255, 255, 255, 0.1);
          border-radius: 15px;
          border: 2px solid rgba(255, 255, 255, 0.2);
        }
      `}</style>
      
      {/* 对家弃牌区（上方） */}
      {renderDiscardArea(2, player2Discards, 'top')}
      
      {/* 上家弃牌区（左侧） */}
      {renderDiscardArea(3, player3Discards, 'left')}
      
      {/* 中央区域 */}
      <div className="center-area">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
          className="text-6xl text-white/30"
        >
          🀄
        </motion.div>
      </div>
      
      {/* 下家弃牌区（右侧） */}
      {renderDiscardArea(1, player1Discards, 'right')}
      
      {/* 我的弃牌区（下方） */}
      {renderDiscardArea(0, player0Discards, 'bottom')}
    </div>
  );
};

export default MahjongTable;
