import React from 'react';
import { motion } from 'framer-motion';
import { useGameStore, selectPlayerHand } from '../stores/gameStore';
import { Tile, MeldType, GangType, calculateRemainingTiles } from '../types/mahjong';
import MahjongTile from './MahjongTile';

interface MahjongTableProps {
  className?: string;
}

const MahjongTable: React.FC<MahjongTableProps> = ({ className }) => {
  const gameState = useGameStore(state => state.gameState);
  const player0Hand = useGameStore(selectPlayerHand(0));
  const player1Hand = useGameStore(selectPlayerHand(1));
  const player2Hand = useGameStore(selectPlayerHand(2));
  const player3Hand = useGameStore(selectPlayerHand(3));
  
  const player0Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[0] || []);
  const player1Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[1] || []);
  const player2Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[2] || []);
  const player3Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[3] || []);

  // 获取所有弃牌，用于在牌桌中心显示
  const allDiscardedTiles = useGameStore(state => state.gameState.discarded_tiles || []);
  
  const remainingTiles = calculateRemainingTiles(gameState);
  const playerNames = ['我', '下家', '对家', '上家'];

  // 渲染碰牌杠牌
  const renderMelds = (melds: any[], position: string) => {
    if (melds.length === 0) return null;
    
    return (
      <div className={`flex ${position === 'left' || position === 'right' ? 'flex-col' : 'flex-row'} gap-2`}>
        {melds.map((meld, index) => (
          <div 
            key={`meld-${index}`}
            className={`flex ${position === 'left' || position === 'right' ? 'flex-col' : 'flex-row'} gap-1 p-1 rounded bg-white bg-opacity-20`}
          >
            {meld.tiles.map((tile: Tile, tileIndex: number) => (
              <div
                key={`meld-tile-${index}-${tileIndex}`}
                className={
                  position === 'left' ? 'transform -rotate-90' :
                  position === 'right' ? 'transform rotate-90' :
                  position === 'top' ? 'transform rotate-180' :
                  ''
                }
              >
                <MahjongTile
                  tile={tile}
                  size="tiny"
                  variant={meld.type === MeldType.GANG && meld.gang_type === GangType.AN_GANG && tileIndex === 1 ? "disabled" : "default"}
                />
              </div>
            ))}
            <div className={`text-xs text-white text-center ${
              position === 'left' || position === 'right' ? 'transform -rotate-90' : ''
            }`}>
              {meld.type === MeldType.PENG ? '碰' : 
               meld.type === MeldType.GANG && meld.gang_type === GangType.MING_GANG ? '明杠' :
               meld.type === MeldType.GANG && meld.gang_type === GangType.AN_GANG ? '暗杠' : '吃'}
            </div>
          </div>
        ))}
      </div>
    );
  };

  // 渲染手牌
  const renderHand = (hand: any, position: string, playerId: number) => {
    const isMyHand = playerId === 0;
    
    return (
      <div className={`flex ${position === 'left' || position === 'right' ? 'flex-col' : 'flex-row'}`} style={{ gap: 0, margin: 0, padding: 0 }}>
        {hand.tiles.map((tile: Tile, index: number) => (
          <div
            key={`hand-${playerId}-${index}`}
            className={`${
              position === 'left' ? 'transform -rotate-90' :
              position === 'right' ? 'transform rotate-90' :
              position === 'top' ? 'transform rotate-180' :
              ''
            } ${index === hand.tiles.length - 1 ? 'last' : ''}`}
            style={{ margin: 0, padding: 0 }}
          >
            <MahjongTile
              tile={tile}
              size="small"
              variant="default"
              seamless={true}
            />
          </div>
        ))}
      </div>
    );
  };

  // 渲染牌桌中心的所有弃牌
  const renderTableCenter = () => {
    return (
      <div className="flex flex-col items-center justify-center h-full space-y-4">
        {/* 剩余牌数显示 */}
        <div className="text-center text-white">
          <motion.div
            animate={{ scale: [1, 1.1, 1] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
            className="text-3xl font-bold mb-1"
          >
            {remainingTiles}
          </motion.div>
          <div className="text-xs opacity-80">剩余牌数</div>
        </div>
        
        {/* 牌桌弃牌区 */}
        {allDiscardedTiles.length > 0 && (
          <div className="bg-white bg-opacity-10 rounded-lg p-3 max-w-sm max-h-40 overflow-y-auto">
            <div className="text-center text-white text-xs mb-2">牌桌弃牌 ({allDiscardedTiles.length}张)</div>
            <div className="flex flex-wrap gap-1 justify-center">
              {allDiscardedTiles.map((tile, index) => (
                <MahjongTile
                  key={`table-discard-${index}`}
                  tile={tile}
                  size="tiny"
                  variant="disabled"
                  animationDelay={index * 0.02}
                />
              ))}
            </div>
          </div>
        )}
      </div>
    );
  };

  // 渲染玩家区域
  const renderPlayerArea = (playerId: number, position: string) => {
    const hand = [player0Hand, player1Hand, player2Hand, player3Hand][playerId];
    const discards = [player0Discards, player1Discards, player2Discards, player3Discards][playerId];
    
    const flexDirection = position === 'left' || position === 'right' ? 'flex-col' : 'flex-row';
    const alignment = position === 'bottom' ? 'items-end' : 
                     position === 'top' ? 'items-start' :
                     position === 'left' ? 'items-start' :
                     'items-end';
    
    return (
      <div className={`flex ${flexDirection} ${alignment} gap-2 h-full w-full`}>
        {/* 玩家标识 */}
        <div className={`text-white text-xs font-bold ${
          position === 'left' || position === 'right' ? 'transform -rotate-90' : ''
        }`}>
          {playerNames[playerId]} ({hand.tiles.length + hand.melds.reduce((sum, meld) => sum + meld.tiles.length, 0)}张)
        </div>
        
        {/* 手牌区域 - 所有玩家都显示手牌 */}
        <div className="flex-shrink-0">
          {renderHand(hand, position, playerId)}
        </div>
        
        {/* 碰牌杠牌区域 */}
        {hand.melds.length > 0 && (
          <div className="flex-shrink-0">
            {renderMelds(hand.melds, position)}
          </div>
        )}
        
        {/* 个人弃牌区域 - 只在有弃牌时显示 */}
        {discards.length > 0 && (
          <div className="flex-1 flex justify-center items-center">
            <div className="bg-white bg-opacity-10 rounded p-1">
              <div className={`text-xs text-white text-center mb-1 ${
                position === 'left' || position === 'right' ? 'transform -rotate-90' : ''
              }`}>
                个人弃牌({discards.length})
              </div>
              <div className={`flex ${position === 'left' || position === 'right' ? 'flex-col' : 'flex-row'} gap-1 flex-wrap max-w-24 max-h-24 overflow-hidden`}>
                {discards.slice(-6).map((tile, index) => (
                  <div
                    key={`discard-${index}`}
                    className={
                      position === 'left' ? 'transform -rotate-90' :
                      position === 'right' ? 'transform rotate-90' :
                      position === 'top' ? 'transform rotate-180' :
                      ''
                    }
                  >
                    <MahjongTile
                      tile={tile}
                      size="tiny"
                      variant="disabled"
                      animationDelay={index * 0.02}
                    />
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className={`w-full h-full min-w-[450px] min-h-[450px] aspect-square bg-gradient-to-br from-green-800 to-green-900 rounded-3xl p-8 grid grid-cols-7 grid-rows-7 gap-3 ${className}`} style={{ width: 'max(800px, min(100vw - 2rem, 100vh - 2rem))', height: 'max(800px, min(100vw - 2rem, 100vh - 2rem))' }}>
      {/* 对家区域（上方） - 调整为更大空间 */}
      <div className="col-span-5 col-start-2 row-start-1 flex items-center justify-center">
        {renderPlayerArea(2, 'top')}
      </div>
      
      {/* 上家区域（左侧） - 调整为更大空间 */}
      <div className="col-start-1 row-span-5 row-start-2 flex items-center justify-center">
        {renderPlayerArea(3, 'left')}
      </div>
      
      {/* 中央区域 - 牌桌中心（剩余牌数 + 所有弃牌） */}
      <div className="col-span-5 row-span-5 col-start-2 row-start-2 flex items-center justify-center bg-white bg-opacity-10 rounded-2xl border-2 border-white border-opacity-20">
        {renderTableCenter()}
      </div>
      
      {/* 下家区域（右侧） - 调整为更大空间 */}
      <div className="col-start-7 row-span-5 row-start-2 flex items-center justify-center">
        {renderPlayerArea(1, 'right')}
      </div>
      
      {/* 我的区域（下方） - 调整为更大空间，现在显示手牌 */}
      <div className="col-span-5 col-start-2 row-start-7 flex items-center justify-center">
        {renderPlayerArea(0, 'bottom')}
      </div>
    </div>
  );
};

export default MahjongTable;
