import React, { useState } from 'react';
import { motion } from 'framer-motion';
import KongDisplay from './KongDisplay';
import { KongMeld, TileType } from '../types/mahjong';

const KongShowcase: React.FC = () => {
  const [selectedPosition, setSelectedPosition] = useState<'bottom' | 'right' | 'top' | 'left'>('bottom');
  const [currentPlayerView, setCurrentPlayerView] = useState<number>(0);

  // 直杠示例数据
  const directKong: KongMeld = {
    id: "kong_001",
    type: "direct_kong",
    card: { type: TileType.WAN, value: 5 },
    tiles: [
      { type: TileType.WAN, value: 5, id: "wan5_1" },
      { type: TileType.WAN, value: 5, id: "wan5_2" },
      { type: TileType.WAN, value: 5, id: "wan5_3" },
      { type: TileType.WAN, value: 5, id: "wan5_4" }
    ],
    source_player: 2,  // 来自玩家2
    kong_player: 0,    // 玩家0杠牌
    timestamp: Date.now()
  };

  // 加杠示例数据
  const addedKong: KongMeld = {
    id: "kong_002", 
    type: "added_kong",
    card: { type: TileType.TIAO, value: 3 },
    tiles: [
      { type: TileType.TIAO, value: 3, id: "tiao3_1" },
      { type: TileType.TIAO, value: 3, id: "tiao3_2" },
      { type: TileType.TIAO, value: 3, id: "tiao3_3" },
      { type: TileType.TIAO, value: 3, id: "tiao3_4" }
    ],
    original_peng_id: "peng_001",
    kong_player: 0,
    timestamp: Date.now()
  };

  // 暗杠示例数据
  const concealedKong: KongMeld = {
    id: "kong_003",
    type: "concealed_kong",
    card: { type: TileType.TONG, value: 8 },
    tiles: [
      { type: TileType.TONG, value: 8, id: "tong8_1" },
      { type: TileType.TONG, value: 8, id: "tong8_2" },
      { type: TileType.TONG, value: 8, id: "tong8_3" },
      { type: TileType.TONG, value: 8, id: "tong8_4" }
    ],
    kong_player: 0,
    timestamp: Date.now()
  };

  const positions = [
    { value: 'bottom' as const, label: '下方(自己)' },
    { value: 'right' as const, label: '右方' },
    { value: 'top' as const, label: '上方' },
    { value: 'left' as const, label: '左方' }
  ] as const;

  const playerViews = [
    { value: 0, label: '玩家1视角' },
    { value: 1, label: '玩家2视角' },
    { value: 2, label: '玩家3视角' },
    { value: 3, label: '玩家4视角' }
  ];

  return (
    <div className="kong-showcase p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
        杠牌显示演示
      </h2>

      {/* 控制面板 */}
      <div className="controls-panel mb-6 space-y-4">
        {/* 位置选择器 */}
        <div className="position-selector">
          <h3 className="text-lg font-semibold mb-3 text-gray-700">选择玩家位置:</h3>
          <div className="flex flex-wrap gap-2">
            {positions.map(pos => (
              <button
                key={pos.value}
                onClick={() => setSelectedPosition(pos.value)}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  selectedPosition === pos.value
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                {pos.label}
              </button>
            ))}
          </div>
        </div>

        {/* 玩家视角选择器 */}
        <div className="player-view-selector">
          <h3 className="text-lg font-semibold mb-3 text-gray-700">当前玩家视角（影响暗杠显示）:</h3>
          <div className="flex flex-wrap gap-2">
            {playerViews.map(view => (
              <button
                key={view.value}
                onClick={() => setCurrentPlayerView(view.value)}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  currentPlayerView === view.value
                    ? 'bg-green-500 text-white'
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                {view.label}
              </button>
            ))}
          </div>
          <p className="text-sm text-gray-600 mt-2">
            暗杠在自己视角下显示为蓝色选中状态，在其他玩家视角下显示为背面
          </p>
        </div>
      </div>

      {/* 杠牌显示区域 */}
      <div className="kong-displays space-y-8">
        {/* 直杠展示 */}
        <div className="direct-kong-section">
          <h3 className="text-lg font-semibold mb-4 text-gray-700 flex items-center">
            <span className="w-3 h-3 bg-red-500 rounded-full mr-2"></span>
            直杠（明杠）- 杠其他玩家的牌
          </h3>
          <div className="bg-gray-50 p-4 rounded-lg">
            <motion.div
              key={`direct-${selectedPosition}-${currentPlayerView}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <KongDisplay
                kong={directKong}
                playerPosition={selectedPosition}
                className="mb-2"
                isCurrentPlayer={currentPlayerView === directKong.kong_player}
              />
            </motion.div>
            <p className="text-sm text-gray-600 mt-2">
              红色箭头指示牌的来源方向，横放的牌表示来自其他玩家
            </p>
          </div>
        </div>

        {/* 加杠展示 */}
        <div className="added-kong-section">
          <h3 className="text-lg font-semibold mb-4 text-gray-700 flex items-center">
            <span className="w-3 h-3 bg-yellow-500 rounded-full mr-2"></span>
            加杠 - 在原碰牌基础上加杠
          </h3>
          <div className="bg-gray-50 p-4 rounded-lg">
            <motion.div
              key={`added-${selectedPosition}-${currentPlayerView}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.1 }}
            >
              <KongDisplay
                kong={addedKong}
                playerPosition={selectedPosition}
                className="mb-2"
                isCurrentPlayer={currentPlayerView === addedKong.kong_player}
              />
            </motion.div>
            <p className="text-sm text-gray-600 mt-2">
              前3张原碰牌正面显示，第4张新加的牌显示背面，黄色"加"字标识
            </p>
          </div>
        </div>

        {/* 暗杠展示 */}
        <div className="concealed-kong-section">
          <h3 className="text-lg font-semibold mb-4 text-gray-700 flex items-center">
            <span className="w-3 h-3 bg-gray-600 rounded-full mr-2"></span>
            暗杠 - 手牌中的四张相同牌
          </h3>
          <div className="bg-gray-50 p-4 rounded-lg">
            <motion.div
              key={`concealed-${selectedPosition}-${currentPlayerView}`}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3, delay: 0.2 }}
            >
              <KongDisplay
                kong={concealedKong}
                playerPosition={selectedPosition}
                className="mb-2"
                isCurrentPlayer={currentPlayerView === concealedKong.kong_player}
              />
            </motion.div>
            <p className="text-sm text-gray-600 mt-2">
              {currentPlayerView === concealedKong.kong_player 
                ? "自己的暗杠：4张牌显示为蓝色选中状态，能看到牌面" 
                : "其他玩家的暗杠：4张牌都显示背面，看不到具体牌面"}
            </p>
          </div>
        </div>
      </div>

      {/* 说明文档 */}
      <div className="instructions mt-8 p-4 bg-blue-50 rounded-lg">
        <h3 className="text-lg font-semibold mb-3 text-blue-800">使用说明:</h3>
        <ul className="space-y-2 text-sm text-blue-700">
          <li className="flex items-start">
            <span className="w-2 h-2 bg-blue-500 rounded-full mt-2 mr-2"></span>
            <span><strong>直杠:</strong> 3张竖放牌 + 1张横放牌，红色箭头指示牌的来源</span>
          </li>
          <li className="flex items-start">
            <span className="w-2 h-2 bg-blue-500 rounded-full mt-2 mr-2"></span>
            <span><strong>加杠:</strong> 原有3张碰牌（正面）+ 1张新牌（背面）带"加"字标识</span>
          </li>
          <li className="flex items-start">
            <span className="w-2 h-2 bg-blue-500 rounded-full mt-2 mr-2"></span>
            <span><strong>暗杠:</strong> 自己视角显示蓝色选中状态（能看到牌面），其他玩家视角显示背面</span>
          </li>
          <li className="flex items-start">
            <span className="w-2 h-2 bg-blue-500 rounded-full mt-2 mr-2"></span>
            <span><strong>悬停提示:</strong> 鼠标悬停在杠牌上可查看详细信息</span>
          </li>
          <li className="flex items-start">
            <span className="w-2 h-2 bg-blue-500 rounded-full mt-2 mr-2"></span>
            <span><strong>响应式设计:</strong> 适配不同玩家位置和屏幕尺寸</span>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default KongShowcase;
