import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import GameBoard from './components/GameBoard';
import AnalysisPanel from './components/AnalysisPanel';
import SettingsPanel from './components/SettingsPanel';
import { useGameStore } from './stores/gameStore';
import { useSettings } from './hooks/useSettings';
import { MahjongAPI } from './utils/api';
import './App.css';

function App() {
  const { setAvailableTiles } = useGameStore();
  const { settings } = useSettings();
  const [showSettings, setShowSettings] = useState(false);

  useEffect(() => {
    // ÂàùÂßãÂåñÊó∂Ëé∑ÂèñÈ∫ªÂ∞ÜÁâå‰ø°ÊÅØ
    const initializeApp = async () => {
      try {
        const tiles = await MahjongAPI.getTileCodes();
        setAvailableTiles(tiles);

        // ÂÅ•Â∫∑Ê£ÄÊü•
        const isHealthy = await MahjongAPI.healthCheck();
        if (!isHealthy) {
          console.warn('ÂêéÁ´ØÊúçÂä°ÂèØËÉΩÊú™Ê≠£Â∏∏ËøêË°å');
        }
      } catch (error) {
        console.error('ÂàùÂßãÂåñÂ∫îÁî®Â§±Ë¥•:', error);
      }
    };

    initializeApp();
  }, [setAvailableTiles]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-blue-50 flex flex-col">
      {/* Â§¥ÈÉ® - Á¥ßÂáëÁâà */}
      <header className="bg-white shadow-sm border-b border-gray-200 flex-shrink-0">
        <div className="max-w-full mx-auto px-4 sm:px-6">
          <div className="flex justify-between items-center h-12">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              className="flex items-center gap-3"
            >
              <div className="text-2xl">üÄÑ</div>
              <div>
                <h1 className="text-lg font-bold text-gray-900">
                  Ê¨¢‰πêÈ∫ªÂ∞ÜËæÖÂä©Â∑•ÂÖ∑
                </h1>
                <p className="text-xs text-gray-500">
                  Êô∫ËÉΩÂàÜÊûê ¬∑ Á≤æÂáÜÂª∫ËÆÆ ¬∑ ÊèêÂçáËÉúÁéá
                </p>
              </div>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              className="flex items-center gap-3"
            >
              <div className="hidden sm:flex items-center gap-2 text-sm text-gray-600">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                <span>Âú®Á∫øÊúçÂä°</span>
              </div>

              {/* ÂΩìÂâç‰ΩøÁî®ÁöÑËÉåÈù¢Ê†∑ÂºèÊåáÁ§∫Âô® */}
              {currentPage === 'game' && (
                <div className="hidden sm:flex items-center gap-2 text-xs text-gray-500">
                  <span>ËÉåÈù¢Ê†∑Âºè:</span>
                  <span className="px-2 py-1 bg-gray-100 rounded text-gray-700">
                    {settings.cardBackStyle === 'elegant' ? 'Á¥†ÈõÖÁôΩÁéâ' :
                     settings.cardBackStyle === 'classic' ? 'ÁªèÂÖ∏ÁªøËâ≤' :
                     settings.cardBackStyle === 'bamboo' ? 'Á´πÈüµÈùíÈ£é' :
                     settings.cardBackStyle === 'cloud' ? '‰∫ëÁ∫πÁ¥†ÁôΩ' :
                     settings.cardBackStyle === 'traditional' ? '‰º†ÁªüÁ∫¢Êú®' :
                     settings.cardBackStyle === 'pure' ? 'ÊûÅÁÆÄÁ∫ØËâ≤' : 'Êú™Áü•'}
                  </span>
                </div>
              )}

              <button 
                onClick={() => setShowSettings(true)}
                className="p-1.5 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-lg transition-colors"
                title="Ê∏∏ÊàèËÆæÁΩÆ"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.996.608 2.296.07 2.572-1.065z" />
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                </svg>
              </button>
            </motion.div>
          </div>
        </div>
      </header>

      {/* ‰∏ªÂÜÖÂÆπÂå∫ - Ê†πÊçÆÈ°µÈù¢Ê®°ÂºèÊòæÁ§∫‰∏çÂêåÂÜÖÂÆπ */}
      <main className="flex-1 flex overflow-hidden">
        {currentPage === 'game' ? (
          <div className="w-full h-full flex">
            {/* Â∑¶‰æßÔºöÊ∏∏ÊàèÈù¢Êùø - Âç†Áî®Êõ¥Â§öÁ©∫Èó¥ */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1 }}
              className="flex-1 min-w-0 p-4"
            >
              <GameBoard className="h-full w-full" cardBackStyle={settings.cardBackStyle} />
            </motion.div>

            {/* Âè≥‰æßÔºöÂàÜÊûêÈù¢Êùø - Âõ∫ÂÆöÂÆΩÂ∫¶ */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 }}
              className="w-80 flex-shrink-0 p-4 pr-6"
            >
              <AnalysisPanel className="h-full" />
            </motion.div>
          </div>
        ) : (
          /* Êù†ÁâåÊºîÁ§∫È°µÈù¢ */
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 }}
            className="w-full h-full flex justify-center items-start pt-8 px-4"
          >
            <div className="max-w-4xl w-full">
              <KongShowcase />
            </div>
          </motion.div>
        )}
      </main>

      {/* Â∫ïÈÉ®‰ø°ÊÅØ - Êõ¥Á¥ßÂáë */}
      <footer className="bg-white border-t border-gray-200 flex-shrink-0">
        <div className="max-w-full mx-auto px-4 sm:px-6 py-2">
          <div className="flex justify-between items-center text-xs text-gray-600">
            <div className="flex items-center gap-3">
              <span>¬© 2024 Ê¨¢‰πêÈ∫ªÂ∞ÜËæÖÂä©Â∑•ÂÖ∑</span>
              <span className="hidden sm:inline">|</span>
              <span className="hidden sm:inline">Êô∫ËÉΩÁÆóÊ≥ïÈ©±Âä®</span>
              {currentPage === 'kong-demo' && (
                <>
                  <span className="hidden sm:inline">|</span>
                  <span className="hidden sm:inline text-blue-600">Êù†ÁâåÊòæÁ§∫ÊºîÁ§∫</span>
                </>
              )}
            </div>

            <div className="flex items-center gap-3">
              <a 
                href="/docs" 
                className="hover:text-gray-900 transition-colors"
              >
                ‰ΩøÁî®ËØ¥Êòé
              </a>
              <a 
                href="/api/docs" 
                target="_blank"
                rel="noopener noreferrer"
                className="hover:text-gray-900 transition-colors"
              >
                APIÊñáÊ°£
              </a>
