import React, { useState } from 'react';
import { AnimatePresence } from 'framer-motion';
import { useGameStore, selectMyHand, selectDiscardedTiles } from '../stores/gameStore';
import { Tile, TileType, createTile, tileToString, tilesEqual } from '../types/mahjong';
import MahjongTile from './MahjongTile';
import MahjongTable from './MahjongTable';

interface GameBoardProps {
  className?: string;
}

const GameBoard: React.FC<GameBoardProps> = ({ className }) => {
  const myHand = useGameStore(selectMyHand());
  const discardedTiles = useGameStore(selectDiscardedTiles());
  const { addTileToHand, removeTileFromHand, addDiscardedTile } = useGameStore();
  
  const [selectedTiles, setSelectedTiles] = useState<Tile[]>([]);
  const [inputMode, setInputMode] = useState<'hand' | 'discard'>('hand');
  const [selectedDiscardPlayer, setSelectedDiscardPlayer] = useState<number>(0);
  const [selectedHandPlayer, setSelectedHandPlayer] = useState<number>(1); // 默认为下家
  
  // 所有可选的牌
  const availableTiles: Tile[] = [];
  
  // 万 1-9
  for (let i = 1; i <= 9; i++) {
    availableTiles.push(createTile(TileType.WAN, i));
  }
  
  // 条 1-9
  for (let i = 1; i <= 9; i++) {
    availableTiles.push(createTile(TileType.TIAO, i));
  }
  
  // 筒 1-9
  for (let i = 1; i <= 9; i++) {
    availableTiles.push(createTile(TileType.TONG, i));
  }
  
  // 字牌 1-7
  for (let i = 1; i <= 7; i++) {
    availableTiles.push(createTile(TileType.ZI, i));
  }
  
  const handleTileClick = (tile: Tile) => {
    if (inputMode === 'hand') {
      // 为当前选中的玩家添加手牌
      addTileToHand(selectedHandPlayer, tile);
    } else {
      addDiscardedTile(tile, selectedDiscardPlayer);
    }
  };
  
  const handleHandTileClick = (tile: Tile) => {
    const isSelected = selectedTiles.some(t => tilesEqual(t, tile));
    
    if (isSelected) {
      setSelectedTiles(prev => prev.filter(t => !tilesEqual(t, tile)));
    } else {
      setSelectedTiles(prev => [...prev, tile]);
    }
  };
  
  const handleDiscardSelected = () => {
    selectedTiles.forEach(tile => {
      removeTileFromHand(0, tile);
      addDiscardedTile(tile, 0);
    });
    setSelectedTiles([]);
  };
  
  const handleClearHand = () => {
    useGameStore.getState().resetGame();
    setSelectedTiles([]);
  };

  const playerNames = ['我', '下家', '对家', '上家'];
  const playerColors = ['text-blue-600', 'text-green-600', 'text-red-600', 'text-yellow-600'];
  
  return (
    <div className={`bg-green-100 rounded-lg p-3 h-full flex flex-col ${className}`}>
      {/* 标题 - 更紧凑 */}
      <div className="mb-3 flex-shrink-0">
        <h2 className="text-xl font-bold text-gray-800 mb-1">🀄 麻将牌局</h2>
        <div className="flex gap-2">
          <span className="text-xs text-gray-600">我的手牌: {myHand.tiles.length}张</span>
          <span className="text-xs text-gray-600">牌桌弃牌: {discardedTiles.length}张</span>
        </div>
      </div>

      {/* 麻将桌布局 - 调整高度和标题 */}
      <div className="mb-3 flex-shrink-0">
        <h3 className="text-base font-semibold text-gray-700 mb-2">麻将牌桌（所有玩家手牌可见）</h3>
        <div className="h-[32rem]">
          <MahjongTable className="h-full" />
        </div>
      </div>
      
      {/* 我的手牌 - 调整间距 */}
      <div className="mb-3 flex-shrink-0">
        <div className="flex justify-between items-center mb-2">
          <h3 className="text-base font-semibold text-gray-700">我的手牌（在牌桌上不显示）</h3>
          <div className="flex gap-2">
            {selectedTiles.length > 0 && (
              <button
                onClick={handleDiscardSelected}
                className="px-2 py-1 bg-red-500 text-white rounded text-xs hover:bg-red-600 transition-colors"
              >
                弃掉选中的牌 ({selectedTiles.length})
              </button>
            )}
            <button
              onClick={handleClearHand}
              className="px-2 py-1 bg-gray-500 text-white rounded text-xs hover:bg-gray-600 transition-colors"
            >
              清空所有手牌
            </button>
          </div>
        </div>
        
        <div className="min-h-16 bg-white/50 rounded-lg p-3 border-2 border-dashed border-gray-300">
          {myHand.tiles.length > 0 ? (
            <div className="flex flex-wrap gap-0">
              <AnimatePresence>
                {myHand.tiles.map((tile, index) => {
                  const isSelected = selectedTiles.some(t => tilesEqual(t, tile));
                  return (
                    <MahjongTile
                      key={`hand-${index}`}
                      tile={tile}
                      variant={isSelected ? 'selected' : 'default'}
                      onClick={() => handleHandTileClick(tile)}
                      animationDelay={index * 0.1}
                    />
                  );
                })}
              </AnimatePresence>
            </div>
          ) : (
            <div className="text-center text-gray-500 py-3 text-sm">
              点击下方的麻将牌来添加到我的手牌，或选择其他玩家添加手牌
            </div>
          )}
        </div>
      </div>
      
      {/* 输入模式切换 - 增加为其他玩家添加手牌的选项 */}
      <div className="mb-3 flex-shrink-0">
        <div className="flex items-center gap-3 flex-wrap">
          <div className="flex bg-gray-200 rounded-lg p-1">
            <button
              onClick={() => setInputMode('hand')}
              className={`px-3 py-1.5 rounded-md text-xs font-medium transition-colors ${
                inputMode === 'hand'
                  ? 'bg-white text-blue-600 shadow-sm'
                  : 'text-gray-600 hover:text-gray-800'
              }`}
            >
              添加到手牌
            </button>
            <button
              onClick={() => setInputMode('discard')}
              className={`px-3 py-1.5 rounded-md text-xs font-medium transition-colors ${
                inputMode === 'discard'
                  ? 'bg-white text-blue-600 shadow-sm'
                  : 'text-gray-600 hover:text-gray-800'
              }`}
            >
              直接弃牌
            </button>
          </div>

          {/* 手牌玩家选择 */}
          {inputMode === 'hand' && (
            <div className="flex items-center gap-2">
              <span className="text-xs text-gray-600">为谁添加手牌:</span>
              <div className="flex gap-1">
                {playerNames.map((name, index) => (
                  <button
                    key={index}
                    onClick={() => setSelectedHandPlayer(index)}
                    className={`px-2 py-1 rounded text-xs font-medium transition-colors ${
                      selectedHandPlayer === index
                        ? `bg-opacity-20 ${playerColors[index].replace('text-', 'bg-').replace('-600', '-200')} ${playerColors[index]} border border-current`
                        : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                    }`}
                  >
                    {name}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* 弃牌对手选择 */}
          {inputMode === 'discard' && (
            <div className="flex items-center gap-2">
              <span className="text-xs text-gray-600">弃牌玩家:</span>
              <div className="flex gap-1">
                {playerNames.map((name, index) => (
                  <button
                    key={index}
                    onClick={() => setSelectedDiscardPlayer(index)}
                    className={`px-2 py-1 rounded text-xs font-medium transition-colors ${
                      selectedDiscardPlayer === index
                        ? `bg-opacity-20 ${playerColors[index].replace('text-', 'bg-').replace('-600', '-200')} ${playerColors[index]} border border-current`
                        : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                    }`}
                  >
                    {name}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
      
      {/* 麻将牌选择区 - 可滚动，占用剩余空间 */}
      <div className="bg-white rounded-lg p-3 border border-gray-300 flex-1 overflow-y-auto">
        <h3 className="text-base font-semibold text-gray-700 mb-3">
          选择麻将牌 
          {inputMode === 'hand' && (
            <span className="text-sm text-gray-500 ml-2">
              (为 {playerNames[selectedHandPlayer]} 添加手牌)
            </span>
          )}
          {inputMode === 'discard' && (
            <span className="text-sm text-gray-500 ml-2">
              (弃牌到牌桌 - {playerNames[selectedDiscardPlayer]})
            </span>
          )}
        </h3>
        
        {/* 万 */}
        <div className="mb-3">
          <h4 className="text-xs font-medium text-gray-600 mb-1.5">万子 (1-9万)</h4>
          <div className="flex gap-1.5 flex-wrap">
            {availableTiles.filter(tile => tile.type === TileType.WAN).map((tile, index) => (
              <MahjongTile
                key={`wan-${tile.value}`}
                tile={tile}
                size="small"
                onClick={() => handleTileClick(tile)}
                animationDelay={index * 0.05}
              />
            ))}
          </div>
        </div>
        
        {/* 条 */}
        <div className="mb-3">
          <h4 className="text-xs font-medium text-gray-600 mb-1.5">条子 (1-9条)</h4>
          <div className="flex gap-1.5 flex-wrap">
            {availableTiles.filter(tile => tile.type === TileType.TIAO).map((tile, index) => (
              <MahjongTile
                key={`tiao-${tile.value}`}
                tile={tile}
                size="small"
                onClick={() => handleTileClick(tile)}
                animationDelay={index * 0.05}
              />
            ))}
          </div>
        </div>
        
        {/* 筒 */}
        <div className="mb-3">
          <h4 className="text-xs font-medium text-gray-600 mb-1.5">筒子 (1-9筒)</h4>
          <div className="flex gap-1.5 flex-wrap">
            {availableTiles.filter(tile => tile.type === TileType.TONG).map((tile, index) => (
              <MahjongTile
                key={`tong-${tile.value}`}
                tile={tile}
                size="small"
                onClick={() => handleTileClick(tile)}
                animationDelay={index * 0.05}
              />
            ))}
          </div>
        </div>
        
        {/* 字牌 */}
        <div className="mb-3">
          <h4 className="text-xs font-medium text-gray-600 mb-1.5">字牌 (东南西北中发白)</h4>
          <div className="flex gap-1.5 flex-wrap">
            {availableTiles.filter(tile => tile.type === TileType.ZI).map((tile, index) => (
              <MahjongTile
                key={`zi-${tile.value}`}
                tile={tile}
                size="small"
                onClick={() => handleTileClick(tile)}
                animationDelay={index * 0.05}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default GameBoard;
