import React from 'react';
import { motion } from 'framer-motion';
import { useGameStore, selectPlayerHand } from '../stores/gameStore';
import { Tile, MeldType, GangType, calculateRemainingTiles } from '../types/mahjong';
import MahjongTile from './MahjongTile';

interface MahjongTableProps {
  className?: string;
}

const MahjongTable: React.FC<MahjongTableProps> = ({ className }) => {
  const gameState = useGameStore(state => state.gameState);
  const player0Hand = useGameStore(selectPlayerHand(0));
  const player1Hand = useGameStore(selectPlayerHand(1));
  const player2Hand = useGameStore(selectPlayerHand(2));
  const player3Hand = useGameStore(selectPlayerHand(3));

  const player0Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[0] || []);
  const player1Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[1] || []);
  const player2Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[2] || []);
  const player3Discards = useGameStore(state => state.gameState.player_discarded_tiles?.[3] || []);

  const remainingTiles = calculateRemainingTiles(gameState);
  const playerNames = ['我', '下家', '对家', '上家'];
  const playerColors = ['bg-blue-50 border-blue-200', 'bg-green-50 border-green-200', 'bg-red-50 border-red-200', 'bg-yellow-50 border-yellow-200'];

  // 渲染碰牌杠牌
  const renderMelds = (melds: any[]) => {
    if (melds.length === 0) return null;

    return (
      <div className="flex gap-3">
        {melds.map((meld, index) => (
          <div key={`meld-${index}`} className="flex flex-col items-center">
            <div className="flex gap-0 mb-1">
              {meld.tiles.map((tile: Tile, tileIndex: number) => (
                <MahjongTile
                  key={`meld-tile-${index}-${tileIndex}`}
                  tile={tile}
                  size="small"
                  variant={meld.type === MeldType.GANG && meld.gang_type === GangType.AN_GANG && tileIndex === 1 ? "disabled" : "default"}
                  seamless={true}
                />
              ))}
            </div>
            <div className="text-xs text-gray-600 font-medium">
              {meld.type === MeldType.PENG ? '碰' : 
               meld.type === MeldType.GANG && meld.gang_type === GangType.MING_GANG ? '明杠' :
               meld.type === MeldType.GANG && meld.gang_type === GangType.AN_GANG ? '暗杠' : '吃'}
            </div>
          </div>
        ))}
      </div>
    );
  };

  // 渲染手牌
  const renderHand = (hand: any) => {
    return (
      <div className="flex gap-0">
        {hand.tiles.map((tile: Tile, index: number) => (
          <MahjongTile
            key={`hand-${index}`}
            tile={tile}
            size="small"
            variant="default"
            seamless={true}
          />
        ))}
      </div>
    );
  };

  // 渲染弃牌
  const renderDiscards = (discards: Tile[]) => {
    if (discards.length === 0) {
      return <div className="text-gray-400 text-sm italic">暂无弃牌</div>;
    }

    return (
      <div className="flex flex-wrap gap-0">
        {discards.map((tile, index) => (
          <MahjongTile
            key={`discard-${index}`}
            tile={tile}
            size="tiny"
            variant="disabled"
            seamless={true}
            animationDelay={index * 0.01}
          />
        ))}
      </div>
    );
  };

  // 渲染单个玩家区域
  const renderPlayerArea = (playerId: number) => {
    const hand = [player0Hand, player1Hand, player2Hand, player3Hand][playerId];
    const discards = [player0Discards, player1Discards, player2Discards, player3Discards][playerId];

    return (
      <div className={`border-2 rounded-lg p-4 ${playerColors[playerId]} transition-all duration-200 hover:shadow-md`}>
        {/* 玩家名称和统计信息 */}
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center gap-2">
            <h3 className="text-lg font-bold text-gray-800">{playerNames[playerId]}</h3>
            <div className="text-sm text-gray-600">
              (手牌: {hand.tiles.length}张 | 碰杠: {hand.melds.length}组 | 弃牌: {discards.length}张)
            </div>
          </div>
        </div>

        {/* 第一行：手牌 + 碰牌杠牌 */}
        <div className="mb-4">
          <div className="flex items-start gap-6">
            {/* 手牌区域 */}
            <div className="flex-1">
              <div className="text-sm font-medium text-gray-700 mb-2">手牌</div>
              {hand.tiles.length > 0 ? (
                renderHand(hand)
              ) : (
                <div className="text-gray-400 text-sm italic">暂无手牌</div>
              )}
            </div>

            {/* 碰牌杠牌区域 */}
            {hand.melds.length > 0 && (
              <div className="flex-shrink-0">
                <div className="text-sm font-medium text-gray-700 mb-2">碰牌/杠牌</div>
                {renderMelds(hand.melds)}
              </div>
            )}
          </div>
        </div>

        {/* 第二行：弃牌 */}
        <div>
          <div className="text-sm font-medium text-gray-700 mb-2">弃牌</div>
          {renderDiscards(discards)}
        </div>
      </div>
    );
  };

  return (
    <div className={`bg-gradient-to-br from-green-50 to-green-100 rounded-2xl p-6 ${className}`}>
      {/* 标题和剩余牌数 */}
      <div className="text-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-2">血战麻将</h2>
        <div className="inline-flex items-center gap-2 bg-white rounded-full px-4 py-2 shadow-sm border">
          <span className="text-sm text-gray-600">剩余牌数:</span>
          <motion.div
            animate={{ scale: [1, 1.05, 1] }}
            transition={{ duration: 3, repeat: Infinity, ease: "easeInOut" }}
            className="text-xl font-bold text-green-600"
          >
            {remainingTiles}
          </motion.div>
        </div>
      </div>

      {/* 玩家区域 - 两列布局 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* 第一列 */}
        <div className="space-y-6">
          {renderPlayerArea(0)} {/* 我 */}
          {renderPlayerArea(2)} {/* 对家 */}
        </div>

        {/* 第二列 */}
        <div className="space-y-6">
          {renderPlayerArea(1)} {/* 下家 */}
          {renderPlayerArea(3)} {/* 上家 */}
        </div>
      </div>
    </div>
  );
};

export default MahjongTable;
