import React from 'react';
import { motion } from 'framer-motion';
import classNames from 'classnames';
import { Tile, tileToString, TileType } from '../types/mahjong';

interface MahjongTileProps {
  tile: Tile;
  size?: 'tiny' | 'small' | 'medium' | 'large';
  variant?: 'default' | 'selected' | 'recommended' | 'disabled' | 'back';
  onClick?: () => void;
  onDoubleClick?: () => void;
  className?: string;
  showBackground?: boolean;
  animationDelay?: number;
  seamless?: boolean;
  direction?: 'horizontal' | 'vertical';
}

const MahjongTile: React.FC<MahjongTileProps> = ({
  tile,
  size = 'medium',
  variant = 'default',
  onClick,
  onDoubleClick,
  className,
  showBackground = true,
  animationDelay = 0,
  seamless = false,
  direction = 'horizontal'
}) => {
  const tileText = tileToString(tile);

  // Â∞∫ÂØ∏Ê†∑Âºè
  const sizeClasses = {
    tiny: 'w-6 h-8 text-xs',
    small: 'w-8 h-10 text-xs',
    medium: 'w-12 h-16 text-sm',
    large: 'w-16 h-20 text-base'
  };

  // Âèò‰ΩìÊ†∑Âºè
  const variantClasses = {
    default: 'bg-white border-gray-300 text-gray-800 hover:bg-gray-50',
    selected: 'bg-blue-100 border-blue-400 text-blue-800 ring-2 ring-blue-300',
    recommended: 'bg-green-100 border-green-400 text-green-800 ring-2 ring-green-300 animate-pulse',
    disabled: 'bg-gray-100 border-gray-200 text-gray-400 cursor-not-allowed',
    back: 'bg-gradient-to-br from-green-600 to-green-800 border-green-700 cursor-not-allowed'
  };

  // Ëä±Ëâ≤È¢úËâ≤
  const getTypeColor = (tileType: TileType): string => {
    switch (tileType) {
      case TileType.WAN:
        return 'text-blue-600';
      case TileType.TIAO:
        return 'text-green-600';
      case TileType.TONG:
        return 'text-red-600';
      case TileType.ZI:
        return 'text-purple-600';
      default:
        return 'text-gray-600';
    }
  };

  // Ê†πÊçÆÊñπÂêëÂíåseamlessÂ±ûÊÄßÁîüÊàêËæπÊ°ÜÂíåÂúÜËßíÊ†∑Âºè
  const getSeamlessClasses = () => {
    if (!seamless) {
      return 'border-2 rounded-lg';
    }

    if (direction === 'horizontal') {
      // Ê∞¥Âπ≥ÊñπÂêëÔºöÁßªÈô§Âè≥ËæπÊ°ÜÔºåÂè™ÊúâÊúÄÂêé‰∏Ä‰∏™ÂÖÉÁ¥†ÊúâÂè≥ËæπÊ°Ü
      return 'border-2 border-r-0 last:border-r-2 rounded-none first:rounded-l-lg last:rounded-r-lg';
    } else {
      // ÂûÇÁõ¥ÊñπÂêëÔºöÁßªÈô§‰∏ãËæπÊ°ÜÔºåÂè™ÊúâÊúÄÂêé‰∏Ä‰∏™ÂÖÉÁ¥†Êúâ‰∏ãËæπÊ°Ü
      return 'border-2 border-b-0 last:border-b-2 rounded-none first:rounded-t-lg last:rounded-b-lg';
    }
  };

  const baseClasses = classNames(
    'relative flex items-center justify-center',
    getSeamlessClasses(),
    'cursor-pointer',
    'font-bold select-none transition-all duration-200',
    seamless ? '' : 'shadow-sm hover:shadow-md',
    sizeClasses[size],
    variantClasses[variant],
    variant === 'back' ? 'text-white' : getTypeColor(tile.type),
    {
      'active:scale-95': onClick && variant !== 'disabled' && variant !== 'back' && !seamless
    },
    className
  );

  const handleClick = () => {
    if (variant !== 'disabled' && variant !== 'back' && onClick) {
      onClick();
    }
  };

  const handleDoubleClick = () => {
    if (variant !== 'disabled' && variant !== 'back' && onDoubleClick) {
      onDoubleClick();
    }
  };

  return (
    <motion.div
      className={baseClasses}
      onClick={handleClick}
      onDoubleClick={handleDoubleClick}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ 
        duration: 0.3, 
        delay: animationDelay,
        type: "spring",
        stiffness: 120
      }}
      whileTap={onClick && variant !== 'disabled' && variant !== 'back' ? { scale: 0.95 } : {}}
    >
      {/* ËÉåÊôØË£ÖÈ•∞ */}
      {showBackground && variant !== 'back' && (
        <div className="absolute inset-0 bg-gradient-to-br from-white/20 to-transparent rounded-lg" />
      )}

      {/* ËÉåÈù¢ÂõæÊ°à */}
      {variant === 'back' ? (
        <div className="relative z-10 flex flex-col items-center justify-center">
          <div className="text-white font-bold text-xs opacity-90">üÄÑ</div>
          <div className="w-full h-0.5 bg-white/30 my-0.5"></div>
          <div className="text-white font-bold text-xs opacity-90">üÄÑ</div>
        </div>
      ) : (
        /* ÁâåÈù¢ÊñáÂ≠ó */
        <span className="relative z-10 font-black">
          {tileText}
        </span>
      )}

      {/* Êé®ËçêÊ†áËØÜ */}
      {variant === 'recommended' && (
        <motion.div
          className="absolute -top-1 -right-1 w-3 h-3 bg-green-500 rounded-full"
          animate={{
            scale: [1, 1.2, 1],
            opacity: [1, 0.7, 1]
          }}
          transition={{
            duration: 1.5,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
      )}

      {/* ÈÄâ‰∏≠Ê†áËØÜ */}
      {variant === 'selected' && (
        <div className="absolute -top-1 -right-1 w-3 h-3 bg-blue-500 rounded-full" />
      )}
    </motion.div>
  );
};

export default MahjongTile;
